import React, { PureComponent } from 'react';

class Avatar extends PureComponent{
    render(){
        const { dataSource } = this.props;

        return (
            <div style={{width: 200, height: 48, lineHeight: '48px', boxShadow: '4px 4px 8px #999'}}>
                <img src={dataSource.avatar} />
                <span style={{verticalAlign: 'text-bottom', marginLeft: 10, fontSize: 26, color: '#666', fontFamily: 'fantasy'}}>{dataSource.nickName}</span>
            </div>
        )
    }
}

export default Avatar;
