import {
	GET_DATASOURCE
} from './actions'

const _ds = (state = {}, action) => {
	switch (action.type) {
		case GET_DATASOURCE:
			return Object.assign({}, state, {
				ds: [{
					id: 1,
					avatar: 'https://cdn.nlark.com/yuque/0/2019/jpeg/anonymous/1557137977726-6119ea8b-5eed-4e01-b03e-8297d359cb9a.jpeg?x-oss-process=image/resize,m_fill,w_48,h_48/format,png',
					nickName: 'WHOAMI',
					desc: 'WHOAMI'
				},{
					id: 2,
					avatar: 'https://gw.alipayobjects.com/zos/rmsportal/cnrhVkzwxjPwAaCfPbdc.png',
					nickName: 'ANTDESIGN',
					desc: 'ANTDESIGN'
				}]
			})
		default:
			return state
	}
}

const _post = (state = { }, action) => {
	switch (action.type) {
	  case INVALIDATE_SUBREDDIT:
	  case RECEIVE_POSTS:
	  case REQUEST_POSTS:
		return {
		  ...state,
		  [action.subreddit]: posts(state[action.subreddit], action)
		}
	  default:
		return state
	}
  }

const rootReducer = combineReducers({
	_post,
	_ds
})

export default Reducers;
