import React, { Component } from 'react';

const DragPropsHoc = WrappedComponent => {
    return class extends Component{
        componentDidMount(){
            console.log(this.comp);

            this.dragEvtBind();
        }

        dragEvtBind = () => {
            let el = this.comp.dragBox;
            this.initDragStyle(el);
            el.addEventListener('mousedown', e => this.mouseDownEvt(e, el));
        }

        initDragStyle = (el) => {
            let style = el.style;
            style.position = 'absolute';
            style.top = el.offsetTop;
            style.left = el.offsetLeft;
        }

        mouseDownEvt = (e, el) => {
            let style = el.style,
                disTop = e.pageY - style.top,
                disLeft = e.pageX - style.left;
            debugger;

            el.addEventListener('mousemove', e => this.mouseMoveEvt(disTop, disLeft, e, el));
        }

        mouseMoveEvt = (disTop = 0, disLeft = 0, e, el) => {
            let style = el.style;
            style.top = e.pageY - disTop + 'px';
            style.left = e.pageX - disLeft + 'px';
        }

        render(){
            return <WrappedComponent ref={comp => this.comp = comp} {...this.props} /> 
        }
    } 
}

export default DragPropsHoc;
