import React, { PureComponent } from 'react';

class Avatar extends PureComponent{
    render(){
        return (
            <div style={{width: 200, height: 48, lineHeight: '48px', boxShadow: '4px 4px 8px #ddd'}}>
                <img src="https://cdn.nlark.com/yuque/0/2019/jpeg/anonymous/1557137977726-6119ea8b-5eed-4e01-b03e-8297d359cb9a.jpeg?x-oss-process=image/resize,m_fill,w_48,h_48/format,png" />
                <span>WHOAMI</span>
            </div>
        )
    }
}

export default Avatar;
