import React, { Component } from 'react';
// import { connect } from 'react-redux'
import Avatar from './components/avatar';
import connectHoc from './hoc/connect';

@connectHoc(({ dsReducer: { ds } }) => ({
	dsReducer: dsReducer,
	dataSource: ds
}))
class App extends Component{
	componentDidMount(){
		this.search();
	}

	search = () => {
		const { dispatch, dsReducer } = this.props;

		dispatch(fetchPostsIfNeeded(dsReducer))

		// dispatch({
		// 	type: 'GET_DATASOURCE',
		// 	payload: {}
		// });
	}

	render(){
		const { dataSource } = this.props;
		return (
			<div style={{padding: 100}}>
				{
					dataSource && dataSource.map(item => <Avatar key={item.id} dataSource={item}/>)
				}
			</div>
		);
	}
}

// export default connect(({ ds }) => ({
// 	ds: ds
// }))(App);
export default App;
