import { combineReducers } from 'redux';
import fetch from 'cross-fetch'
import posts from '../util';
import {
	GET_DATASOURCE,
	REQUEST_POSTS,
	RECEIVE_POSTS
} from './actions'

const dsReducer = (state = {}, action) => {
	switch (action.type) {
		case GET_DATASOURCE:
			return Object.assign({}, state, {
				ds: [{
					id: 1,
					avatar: 'https://cdn.nlark.com/yuque/0/2019/jpeg/anonymous/1557137977726-6119ea8b-5eed-4e01-b03e-8297d359cb9a.jpeg?x-oss-process=image/resize,m_fill,w_48,h_48/format,png',
					nickName: 'WHOAMI',
					desc: 'WHOAMI'
				},{
					id: 2,
					avatar: 'https://gw.alipayobjects.com/zos/rmsportal/cnrhVkzwxjPwAaCfPbdc.png',
					nickName: 'ANTDESIGN',
					desc: 'ANTDESIGN'
				}]
			})
		default:
			return state
	}
}

const postReducer = (state = {}, action) => {
	switch (action.type) {
		case RECEIVE_POSTS:
		case REQUEST_POSTS:
			return fetch(`/src/mock/mock.json`).then(response => {
				debugger;
				response
			});
		default:
			return state
	}
}

const rootReducer = combineReducers({
	postReducer,
	dsReducer
})

export default rootReducer;
