import React, { Component } from 'react';

const DragPropsHoc = WrappedComponent => {
    return class extends Component{
        componentDidMount(){
            console.log(this.comp);

            this.dragEvtBind();
        }

        dragEvtBind = () => {
            let el = this.comp.dragBox;
            this.initDragStyle(el);
            el.addEventListener('mousedown', this.mouseDownEvt);
        }

        initDragStyle = (el) => {
            let style = el.style;
            style.position = 'absolute';
            style.top = el.offsetTop;
            style.left = el.offsetLeft;
        }

        mouseDownEvt = e => {
            let style = el.style,
                downTop = style.top,
                downLeft = style.left;

            el.addEventListener('mousemove', e => this.mouseMoveEvt(downTop, downLeft, e));
        }

        mouseMoveEvt = (downTop = 0, downLeft = 0, e) => {
            let style = el.style,
                moveTop = style.top,
                moveLeft = style.left;
            debugger;
        }

        render(){
            return <WrappedComponent ref={comp => this.comp = comp} {...this.props} /> 
        }
    } 
}

export default DragPropsHoc;
